import {
  Text,
  View,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Dimensions,
} from "react-native";
import { COLORS, SIZE } from "../style";

//Icons
import { Ionicons, FontAwesome5 } from "@expo/vector-icons";

const windowHeight = Dimensions.get("window").height;

const HomeScreen = ({ navigation }) => {
  const openUserView = () => {
    console.alert("No user view for the moment.");
  };

  return (
    <SafeAreaView style={styles.background}>
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.navigate("Infos")}
          accessibilityLabel="Access to information on app"
          style={styles.roundButton}
        >
          <Ionicons
            name="information-circle"
            size={SIZE.iconSize}
            color={COLORS.darkBlue}
          />
        </TouchableOpacity>
        <TouchableOpacity
          onPress={openUserView}
          accessibilityLabel="Open user infos"
          style={styles.roundButton}
        >
          <FontAwesome5
            name="user-cog"
            size={SIZE.smallIconSize}
            color={COLORS.darkBlue}
          />
        </TouchableOpacity>
      </View>
      <View style={styles.titleContainer}>
        <Text style={styles.h1}>Sillage Carbone</Text>
      </View>

      <View style={styles.historicContainer}>
        <Text>Historique en flatlist</Text>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  background: {
    backgroundColor: COLORS.lightBlue,
    height: windowHeight,
    flex: 1,
    justifyContent: "space-between",
  },
  header: { flexDirection: "row", justifyContent: "space-between", alignItems: "center" },
  roundButton: {
    width: 100,
    height: 60,
    justifyContent: "center",
    alignItems: "center",
    padding: 10,
    borderRadius: 10,
    backgroundColor: COLORS.lightBlue,
  },
  titleContainer: { justifyContent: "center", alignItems: "center" },
  h1: {
    fontSize: 22,
    fontWeight: "bold",
    color: COLORS.darkBlue,
  },
  historicContainer: {
    justifyContent: "center",
    alignItems: "center",
    paddingBottom: 20,
  },
});

export default HomeScreen;
