import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Text, View, StyleSheet, Dimensions, SafeAreaView } from "react-native";

import SecondaryHomeScreen from "./SecondaryHomeScreen";
import ScanScreen from "./ScanScreen";

import { MaterialCommunityIcons } from "@expo/vector-icons";
import { COLORS } from "../style";

const ScanTabScreen = () => {
  const ScanTab = createBottomTabNavigator();

  return (
    <ScanTab.Navigator
      initialRouteName="Scan"
      screenOptions={{
        screenOptions,
        // borderRadius: 15,
        // position: "absolute",
        // bottom: 25,
        // left: 20,
        // right: 20,
        // height: 90,
        // backgroundColor: COLORS.darkBlue,
        // elevation: 0,
        // ...styles.shadow,
      }}
    >
      {/* -- Home Page -- */}
      <ScanTab.Screen
        name="SecondaryHome"
        component={SecondaryHomeScreen}
        options={{
          tabBarIcon: ({ color, size }) => (
            <MaterialCommunityIcons name="lighthouse" size={size} color={color} />
          ),
          tabBarLabel: "Port base",
          headerShown: false,
        }}
      />
      {/* -- Scan Page -- */}
      <ScanTab.Screen
        name="Scan"
        component={ScanScreen}
        options={{
          tabBarIcon: ({ color, size }) => (
            <MaterialCommunityIcons name="barcode-scan" size={size} color={color} />
          ),
          headerShown: false,
        }}
      />
    </ScanTab.Navigator>
  );
};

const screenOptions = {
  headerShown: false,
  tabBarStyle: {
    backgroundColor: COLORS.darkBlue,
    height: 100,
  },
  tabBarItemStyle: {
    backgroundColor: "#00ff00",
    margin: 5,
    borderRadius: 10,
  },
  tabBarActiveTintColor: COLORS.whiteBlue,
  tabBarInactiveTintColor: COLORS.lightBlue,
};

const styles = StyleSheet.create({
  shadow: {
    shadowColor: "#7F5DF0",
    shadowOffset: { width: 0, height: 10 },
    shadowOpacity: 0.25,
    shadowRadius: 3.5,
    elevation: 5,
  },
});
export default ScanTabScreen;
